$(document).ready((function(){new Vue({el:"#load",data:()=>({site_url:window.site_url,post_id:window.post_id,posts:null,loading:!0,errored:!0}),mounted(){axios.get(this.site_url+"/wp-json/wp/v2/pages/"+this.post_id).then((s=>{this.posts=s.data})).catch((s=>{this.errored=!1})).then((()=>{this.loading=!1,$(".real").css("display","block"),$(".article-content").html(this.posts.content.rendered).attr("style",""),$(".single-h2").html(this.posts.title.rendered).attr("style",""),$(".article-list-footer").html('<span class="article-list-date">'+this.posts.post_date+'</span><span class="article-list-divider">&nbsp;&nbsp;/&nbsp;&nbsp;</span><span class="article-list-minutes">'+this.posts.post_metas.views+"&nbsp;Views</span>").attr("style","")}))}})}));